import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Separator } from "@/components/ui/separator";
import { Loader2, Upload, User, Trash2, Check, X, AlertTriangle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

// Profile update schema - subset of insertUserSchema for profile editing
const profileUpdateSchema = z.object({
  name: z.string().min(1, "Name is required").max(100, "Name too long"),
  username: z.string()
    .optional()
    .refine((val) => !val || (val.length >= 3 && val.length <= 30), "Username must be between 3 and 30 characters")
    .refine((val) => !val || /^[a-zA-Z0-9_-]+$/.test(val), "Username can only contain letters, numbers, underscores, and hyphens")
    .transform(val => val && val.toLowerCase()),
  bio: z.string().max(500, "Bio too long").optional(),
  location: z.string().max(100, "Location too long").optional(),
  profileImageUrl: z.string().optional().or(z.literal("")), // Accept any string (including relative paths from object storage)
});

type ProfileUpdateData = z.infer<typeof profileUpdateSchema>;

interface EditProfileDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  user: {
    id: string;
    name: string;
    username?: string;
    bio?: string;
    avatar?: string;
    location?: string;
  };
}

interface UsernameCheckResponse {
  available: boolean;
  message: string;
}

export function EditProfileDialog({ open, onOpenChange, user }: EditProfileDialogProps) {
  const [isUploading, setIsUploading] = useState(false);
  const [isCheckingUsername, setIsCheckingUsername] = useState(false);
  const [usernameStatus, setUsernameStatus] = useState<'idle' | 'available' | 'taken' | 'invalid'>('idle');
  const [usernameMessage, setUsernameMessage] = useState('');
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<ProfileUpdateData>({
    resolver: zodResolver(profileUpdateSchema),
    defaultValues: {
      name: user.name || "",
      username: user.username || undefined,
      bio: user.bio || "",
      location: user.location || "",
      profileImageUrl: user.avatar || "",
    },
  });

  const watchedUsername = form.watch('username');

  // Username availability checking
  useEffect(() => {
    const checkUsername = async () => {
      if (!watchedUsername || watchedUsername.length < 3) {
        setUsernameStatus('idle');
        setUsernameMessage('');
        return;
      }

      // Don't check if it's the current username
      if (watchedUsername === user.username) {
        setUsernameStatus('idle');
        setUsernameMessage('');
        return;
      }

      setIsCheckingUsername(true);
      setUsernameStatus('idle');

      try {
        const response = await fetch(`/api/users/check-username/${encodeURIComponent(watchedUsername)}`);
        const data: UsernameCheckResponse = await response.json();

        if (data.available) {
          setUsernameStatus('available');
        } else {
          setUsernameStatus('taken');
        }
        setUsernameMessage(data.message);
      } catch (error) {
        setUsernameStatus('invalid');
        setUsernameMessage('Failed to check availability');
      } finally {
        setIsCheckingUsername(false);
      }
    };

    const timeoutId = setTimeout(checkUsername, 500);
    return () => clearTimeout(timeoutId);
  }, [watchedUsername, user.username]);

  const updateProfileMutation = useMutation({
    mutationFn: async (data: ProfileUpdateData) => {
      // Split full name into firstName and lastName for backend
      const nameParts = data.name.trim().split(' ');
      const firstName = nameParts[0] || '';
      const lastName = nameParts.length > 1 ? nameParts.slice(1).join(' ') : '';

      const response = await apiRequest('PUT', '/api/users/me', {
        firstName,
        lastName,
        username: data.username || null,
        bio: data.bio || null,
        location: data.location || null,
        profileImageUrl: data.profileImageUrl || null,
      });
      return { updatedData: data, response: await response.json() };
    },
    onSuccess: (result) => {
      const updatedData = result.updatedData;

      toast({
        title: "Profile updated successfully!",
        description: "Your profile changes have been saved.",
      });

      // Update cache directly for immediate UI update
      queryClient.setQueryData(['/api/user'], (oldData: any) => {
        if (oldData) {
          return { 
            ...oldData, 
            username: updatedData.username,
            profileImageUrl: updatedData.profileImageUrl 
          };
        }
        return oldData;
      });

      // Update user stats query specifically
      const userStatsQueryKey = ['/api/users', user.id, 'stats'];
      queryClient.setQueryData(userStatsQueryKey, (oldData: any) => {
        if (oldData) {
          return { 
            ...oldData, 
            username: updatedData.username,
            profileImageUrl: updatedData.profileImageUrl 
          };
        }
        return oldData;
      });

      // Invalidate and refetch user data
      queryClient.invalidateQueries({ queryKey: ['/api/user'] });
      queryClient.invalidateQueries({ queryKey: ['/api/users'] });

      onOpenChange(false);
    },
    onError: (error: any) => {
      toast({
        title: "Failed to update profile",
        description: error.message || "Something went wrong. Please try again.",
        variant: "destructive",
      });
    },
  });

  const deleteAccountMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('DELETE', '/api/users/me');
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Account deleted successfully",
        description: "Your account and all data have been permanently deleted.",
      });

      // Clear all cached data
      queryClient.clear();

      // Redirect to logout
      setTimeout(() => {
        window.location.href = "/api/logout";
      }, 1000);
    },
    onError: (error: any) => {
      toast({
        title: "Failed to delete account",
        description: error.message || "Something went wrong. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleDeleteAccount = () => {
    deleteAccountMutation.mutate();
  };

  const getUsernameStatusIcon = () => {
    if (isCheckingUsername) {
      return <Loader2 className="h-4 w-4 animate-spin text-muted-foreground" />;
    }

    switch (usernameStatus) {
      case 'available':
        return <Check className="h-4 w-4 text-green-500" />;
      case 'taken':
      case 'invalid':
        return <X className="h-4 w-4 text-red-500" />;
      default:
        return null;
    }
  };

  const getUsernameStatusColor = () => {
    switch (usernameStatus) {
      case 'available':
        return 'text-green-600';
      case 'taken':
      case 'invalid':
        return 'text-red-600';
      default:
        return 'text-muted-foreground';
    }
  };

  const handleImageUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    // Validate file type
    if (!file.type.startsWith('image/')) {
      toast({
        title: "Invalid file type",
        description: "Please select an image file.",
        variant: "destructive",
      });
      return;
    }

    // Validate file size (max 5MB)
    if (file.size > 5 * 1024 * 1024) {
      toast({
        title: "File too large",
        description: "Please select an image smaller than 5MB.",
        variant: "destructive",
      });
      return;
    }

    setIsUploading(true);
    try {
      // Create FormData for file upload
      const formData = new FormData();
      formData.append('file', file);

      // Upload to object storage (assuming there's an upload endpoint)
      const uploadResponse = await fetch('/api/upload/profile-image', {
        method: 'POST',
        body: formData,
      });

      if (!uploadResponse.ok) {
        throw new Error('Failed to upload image');
      }

      const { url } = await uploadResponse.json();
      form.setValue('profileImageUrl', url);

      toast({
        title: "Image uploaded successfully!",
        description: "Your profile image has been updated.",
      });
    } catch (error: any) {
      toast({
        title: "Upload failed",
        description: error.message || "Failed to upload image. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsUploading(false);
    }
  };

  const onSubmit = (data: ProfileUpdateData) => {
    updateProfileMutation.mutate(data);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-md max-h-[90vh] overflow-y-auto" data-testid="dialog-edit-profile">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <User className="h-5 w-5" />
            Edit Profile
          </DialogTitle>
          <DialogDescription>
            Update your profile information and photo.
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            {/* Profile Photo Upload */}
            <div className="flex flex-col items-center gap-4">
              <Avatar className="w-24 h-24" data-testid="avatar-profile-edit">
                <AvatarImage src={form.watch('profileImageUrl') || user.avatar} alt={user.name} />
                <AvatarFallback className="text-xl">{user.name.charAt(0)}</AvatarFallback>
              </Avatar>

              <div className="relative">
                <Input
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
                  disabled={isUploading}
                  data-testid="input-profile-image"
                />
                <Button
                  type="button"
                  variant="outline"
                  size="sm"
                  disabled={isUploading}
                  className="pointer-events-none"
                  data-testid="button-upload-image"
                >
                  {isUploading ? (
                    <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                  ) : (
                    <Upload className="h-4 w-4 mr-2" />
                  )}
                  {isUploading ? "Uploading..." : "Change Photo"}
                </Button>
              </div>
            </div>

            {/* Name Field */}
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Name</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="Your display name"
                      data-testid="input-name"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Username Field */}
            <FormField
              control={form.control}
              name="username"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Username</FormLabel>
                  <FormControl>
                    <div className="relative">
                      <div className="absolute left-3 top-1/2 -translate-y-1/2 text-muted-foreground">
                        @
                      </div>
                      <Input 
                        {...field}
                        placeholder="your_username"
                        className="pl-8 pr-10"
                        data-testid="input-username"
                        autoComplete="off"
                      />
                      <div className="absolute right-3 top-1/2 -translate-y-1/2">
                        {getUsernameStatusIcon()}
                      </div>
                    </div>
                  </FormControl>
                  <FormMessage />
                  {usernameMessage && (
                    <p className={`text-sm ${getUsernameStatusColor()}`} data-testid="username-availability-message">
                      {usernameMessage}
                    </p>
                  )}
                </FormItem>
              )}
            />

            {/* Bio Field */}
            <FormField
              control={form.control}
              name="bio"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Bio</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Tell us about yourself..."
                      className="resize-none"
                      rows={3}
                      data-testid="input-bio"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Location Field */}
            <FormField
              control={form.control}
              name="location"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Location</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="City, Country"
                      data-testid="input-location"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Danger Zone */}
            <div className="pt-6">
              <Separator className="mb-4" />
              <div className="space-y-4">
                <div>
                  <h3 className="text-lg font-medium text-destructive flex items-center gap-2">
                    <AlertTriangle className="h-5 w-5" />
                    Danger Zone
                  </h3>
                  <p className="text-sm text-muted-foreground mt-1">
                    Permanently delete your account and all associated data.
                  </p>
                </div>

                <AlertDialog>
                  <AlertDialogTrigger asChild>
                    <Button
                      type="button"
                      variant="destructive"
                      size="sm"
                      className="gap-2"
                      data-testid="button-delete-account"
                    >
                      <Trash2 className="h-4 w-4" />
                      Delete Account
                    </Button>
                  </AlertDialogTrigger>
                  <AlertDialogContent data-testid="dialog-confirm-delete">
                    <AlertDialogHeader>
                      <AlertDialogTitle className="flex items-center gap-2 text-destructive">
                        <AlertTriangle className="h-5 w-5" />
                        Delete Account Permanently?
                      </AlertDialogTitle>
                      <AlertDialogDescription className="space-y-2">
                        <p>This action cannot be undone. This will permanently delete your account and remove all of your data from our servers, including:</p>
                        <ul className="list-disc list-inside space-y-1 text-sm">
                          <li>Your profile and personal information</li>
                          <li>All your concert reviews and ratings</li>
                          <li>Your concert history and wishlist</li>
                          <li>All follows and social connections</li>
                        </ul>
                        <p className="font-medium">Are you absolutely sure you want to delete your account?</p>
                      </AlertDialogDescription>
                    </AlertDialogHeader>
                    <AlertDialogFooter>
                      <AlertDialogCancel data-testid="button-cancel-delete">Cancel</AlertDialogCancel>
                      <AlertDialogAction
                        onClick={handleDeleteAccount}
                        disabled={deleteAccountMutation.isPending}
                        className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                        data-testid="button-confirm-delete"
                      >
                        {deleteAccountMutation.isPending ? (
                          <>
                            <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                            Deleting...
                          </>
                        ) : (
                          <>
                            <Trash2 className="h-4 w-4 mr-2" />
                            Delete Account
                          </>
                        )}
                      </AlertDialogAction>
                    </AlertDialogFooter>
                  </AlertDialogContent>
                </AlertDialog>
              </div>
            </div>

            <DialogFooter>
              <Button 
                type="button" 
                variant="outline" 
                onClick={() => onOpenChange(false)}
                data-testid="button-cancel-edit"
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                disabled={updateProfileMutation.isPending || isUploading || (!!watchedUsername && watchedUsername.length >= 3 && watchedUsername !== user.username && usernameStatus === 'taken')}
                data-testid="button-save-profile"
              >
                {updateProfileMutation.isPending ? (
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                ) : null}
                Save Changes
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

server/objectAcl.ts.content:
import { File } from "@google-cloud/storage";

const ACL_POLICY_METADATA_KEY = "custom:aclPolicy";

// The type of the access group.
//
// Can be flexibly defined according to the use case.
//
// Examples:
// - USER_LIST: the users from a list stored in the database;
// - EMAIL_DOMAIN: the users whose email is in a specific domain;
// - GROUP_MEMBER: the users who are members of a specific group;
// - SUBSCRIBER: the users who are subscribers of a specific service / content
//   creator.
export enum ObjectAccessGroupType {}

// The logic user group that can access the object.
export interface ObjectAccessGroup {
  // The type of the access group.
  type: ObjectAccessGroupType;
  // The logic id that is enough to identify the qualified group members.
  //
  // It may have different format for different types. For example:
  // - for USER_LIST, the id could be the user list db entity id, and the
  //   user list db entity could contain a bunch of user ids. User needs
  //   to be a member of the user list to be able to access the object.
  // - for EMAIL_DOMAIN, the id could be the email domain, and the user needs
  //   to have an email with the domain to be able to access the object.
  // - for GROUP_MEMBER, the id could be the group db entity id, and the
  //   group db entity could contain a bunch of user ids. User needs to be
  //   a member of the group to be able to access the object.
  // - for SUBSCRIBER, the id could be the subscriber db entity id, and the
  //   subscriber db entity could contain a bunch of user ids. User needs to
  //   be a subscriber to be able to access the object.
  id: string;
}

export enum ObjectPermission {
  READ = "read",
  WRITE = "write",
}

export interface ObjectAclRule {
  group: ObjectAccessGroup;
  permission: ObjectPermission;
}

// The ACL policy of the object.
// This would be set as part of the object custom metadata:
// - key: "custom:aclPolicy"
// - value: JSON string of the ObjectAclPolicy object.
export interface ObjectAclPolicy {
  owner: string;
  visibility: "public" | "private";
  aclRules?: Array<ObjectAclRule>;
}

// Check if the requested permission is allowed based on the granted permission.
function isPermissionAllowed(
  requested: ObjectPermission,
  granted: ObjectPermission,
): boolean {
  // Users granted with read or write permissions can read the object.
  if (requested === ObjectPermission.READ) {
    return [ObjectPermission.READ, ObjectPermission.WRITE].includes(granted);
  }

  // Only users granted with write permissions can write the object.
  return granted === ObjectPermission.WRITE;
}

// The base class for all access groups.
//
// Different types of access groups can be implemented according to the use case.
abstract class BaseObjectAccessGroup implements ObjectAccessGroup {
  constructor(
    public readonly type: ObjectAccessGroupType,
    public readonly id: string,
  ) {}

  // Check if the user is a member of the group.
  public abstract hasMember(userId: string): Promise<boolean>;
}

function createObjectAccessGroup(
  group: ObjectAccessGroup,
): BaseObjectAccessGroup {
  switch (group.type) {
    // Implement the case for each type of access group to instantiate.
    //
    // For example:
    // case "USER_LIST":
    //   return new UserListAccessGroup(group.id);
    // case "EMAIL_DOMAIN":
    //   return new EmailDomainAccessGroup(group.id);
    // case "GROUP_MEMBER":
    //   return new GroupMemberAccessGroup(group.id);
    // case "SUBSCRIBER":
    //   return new SubscriberAccessGroup(group.id);
    default:
      throw new Error(`Unknown access group type: ${group.type}`);
  }
}

// Sets the ACL policy to the object metadata.
export async function setObjectAclPolicy(
  objectFile: File,
  aclPolicy: ObjectAclPolicy,
): Promise<void> {
  const [exists] = await objectFile.exists();
  if (!exists) {
    throw new Error(`Object not found: ${objectFile.name}`);
  }

  await objectFile.setMetadata({
    metadata: {
      [ACL_POLICY_METADATA_KEY]: JSON.stringify(aclPolicy),
    },
  });
}

// Gets the ACL policy from the object metadata.
export async function getObjectAclPolicy(
  objectFile: File,
): Promise<ObjectAclPolicy | null> {
  const [metadata] = await objectFile.getMetadata();
  const aclPolicy = metadata?.metadata?.[ACL_POLICY_METADATA_KEY];
  if (!aclPolicy) {
    return null;
  }
  return JSON.parse(aclPolicy as string);
}

// Checks if the user can access the object.
export async function canAccessObject({
  userId,
  objectFile,
  requestedPermission,
}: {
  userId?: string;
  objectFile: File;
  requestedPermission: ObjectPermission;
}): Promise<boolean> {
  // When this function is called, the acl policy is required.
  const aclPolicy = await getObjectAclPolicy(objectFile);
  if (!aclPolicy) {
    return false;
  }

  // Public objects are always accessible for read.
  if (
    aclPolicy.visibility === "public" &&
    requestedPermission === ObjectPermission.READ
  ) {
    return true;
  }

  // Access control requires the user id.
  if (!userId) {
    return false;
  }

  // The owner of the object can always access it.
  if (aclPolicy.owner === userId) {
    return true;
  }

  // Go through the ACL rules to check if the user has the required permission.
  for (const rule of aclPolicy.aclRules || []) {
    const accessGroup = createObjectAccessGroup(rule.group);
    if (
      (await accessGroup.hasMember(userId)) &&
      isPermissionAllowed(requestedPermission, rule.permission)
    ) {
      return true;
    }
  }

  return false;
}

server/objectStorage.ts.content:
import { Storage, File } from "@google-cloud/storage";
import { Response } from "express";
import { randomUUID } from "crypto";
import type {
  ObjectAclPolicy,
} from "./objectAcl.js";
import {
  ObjectPermission,
  canAccessObject,
  getObjectAclPolicy,
  setObjectAclPolicy,
} from "./objectAcl.js";

const REPLIT_SIDECAR_ENDPOINT = "http://127.0.0.1:1106";

// The object storage client is used to interact with the object storage service.
export const objectStorageClient = new Storage({
  credentials: {
    audience: "replit",
    subject_token_type: "access_token",
    token_url: `${REPLIT_SIDECAR_ENDPOINT}/token`,
    type: "external_account",
    credential_source: {
      url: `${REPLIT_SIDECAR_ENDPOINT}/credential`,
      format: {
        type: "json",
        subject_token_field_name: "access_token",
      },
    },
    universe_domain: "googleapis.com",
  },
  projectId: "",
});

export class ObjectNotFoundError extends Error {
  constructor() {
    super("Object not found");
    this.name = "ObjectNotFoundError";
    Object.setPrototypeOf(this, ObjectNotFoundError.prototype);
  }
}

// The object storage service is used to interact with the object storage service.
export class ObjectStorageService {
  constructor() {}

  // Gets the public object search paths.
  getPublicObjectSearchPaths(): Array<string> {
    const pathsStr = process.env.PUBLIC_OBJECT_SEARCH_PATHS || "";
    const paths = Array.from(
      new Set(
        pathsStr
          .split(",")
          .map((path) => path.trim())
          .filter((path) => path.length > 0)
      )
    );
    if (paths.length === 0) {
      throw new Error(
        "PUBLIC_OBJECT_SEARCH_PATHS not set. Create a bucket in 'Object Storage' " +
          "tool and set PUBLIC_OBJECT_SEARCH_PATHS env var (comma-separated paths)."
      );
    }
    return paths;
  }

  // Gets the private object directory.
  getPrivateObjectDir(): string {
    const dir = process.env.PRIVATE_OBJECT_DIR || "";
    if (!dir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }
    return dir;
  }

  // Search for a public object from the search paths.
  async searchPublicObject(filePath: string): Promise<File | null> {
    for (const searchPath of this.getPublicObjectSearchPaths()) {
      const fullPath = `${searchPath}/${filePath}`;

      // Full path format: /<bucket_name>/<object_name>
      const { bucketName, objectName } = parseObjectPath(fullPath);
      const bucket = objectStorageClient.bucket(bucketName);
      const file = bucket.file(objectName);

      // Check if file exists
      const [exists] = await file.exists();
      if (exists) {
        return file;
      }
    }

    return null;
  }

  // Downloads an object to the response.
  async downloadObject(file: File, res: Response, cacheTtlSec: number = 3600) {
    try {
      // Get file metadata
      const [metadata] = await file.getMetadata();
      // Get the ACL policy for the object.
      const aclPolicy = await getObjectAclPolicy(file);
      const isPublic = aclPolicy?.visibility === "public";
      // Set appropriate headers
      res.set({
        "Content-Type": metadata.contentType || "application/octet-stream",
        "Content-Length": metadata.size,
        "Cache-Control": `${
          isPublic ? "public" : "private"
        }, max-age=${cacheTtlSec}`,
      });

      // Stream the file to the response
      const stream = file.createReadStream();

      stream.on("error", (err) => {
        console.error("Stream error:", err);
        if (!res.headersSent) {
          res.status(500).json({ error: "Error streaming file" });
        }
      });

      stream.pipe(res);
    } catch (error) {
      console.error("Error downloading file:", error);
      if (!res.headersSent) {
        res.status(500).json({ error: "Error downloading file" });
      }
    }
  }

  // Gets the upload URL for an object entity.
  async getObjectEntityUploadURL(): Promise<string> {
    const privateObjectDir = this.getPrivateObjectDir();
    if (!privateObjectDir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }

    const objectId = randomUUID();
    const fullPath = `${privateObjectDir}/uploads/${objectId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  // Gets the object entity file from the object path.
  async getObjectEntityFile(objectPath: string): Promise<File> {
    if (!objectPath.startsWith("/objects/")) {
      throw new ObjectNotFoundError();
    }

    const parts = objectPath.slice(1).split("/");
    if (parts.length < 2) {
      throw new ObjectNotFoundError();
    }

    const entityId = parts.slice(1).join("/");
    let entityDir = this.getPrivateObjectDir();
    if (!entityDir.endsWith("/")) {
      entityDir = `${entityDir}/`;
    }
    const objectEntityPath = `${entityDir}${entityId}`;
    const { bucketName, objectName } = parseObjectPath(objectEntityPath);
    const bucket = objectStorageClient.bucket(bucketName);
    const objectFile = bucket.file(objectName);
    const [exists] = await objectFile.exists();
    if (!exists) {
      throw new ObjectNotFoundError();
    }
    return objectFile;
  }

  normalizeObjectEntityPath(
    rawPath: string,
  ): string {
    if (!rawPath.startsWith("https://storage.googleapis.com/")) {
      return rawPath;
    }

    // Extract the path from the URL by removing query parameters and domain
    const url = new URL(rawPath);
    const rawObjectPath = url.pathname;

    let objectEntityDir = this.getPrivateObjectDir();
    if (!objectEntityDir.endsWith("/")) {
      objectEntityDir = `${objectEntityDir}/`;
    }

    if (!rawObjectPath.startsWith(objectEntityDir)) {
      return rawObjectPath;
    }

    // Extract the entity ID from the path
    const entityId = rawObjectPath.slice(objectEntityDir.length);
    return `/objects/${entityId}`;
  }

  // Tries to set the ACL policy for the object entity and return the normalized path.
  async trySetObjectEntityAclPolicy(
    rawPath: string,
    aclPolicy: ObjectAclPolicy
  ): Promise<string> {
    const normalizedPath = this.normalizeObjectEntityPath(rawPath);
    if (!normalizedPath.startsWith("/")) {
      return normalizedPath;
    }

    const objectFile = await this.getObjectEntityFile(normalizedPath);
    await setObjectAclPolicy(objectFile, aclPolicy);
    return normalizedPath;
  }

  // Checks if the user can access the object entity.
  async canAccessObjectEntity({
    userId,
    objectFile,
    requestedPermission,
  }: {
    userId?: string;
    objectFile: File;
    requestedPermission?: ObjectPermission;
  }): Promise<boolean> {
    return canAccessObject({
      userId,
      objectFile,
      requestedPermission: requestedPermission ?? ObjectPermission.READ,
    });
  }

  // Uploads a file to private storage and returns the public path
  async uploadFile(
    buffer: Buffer,
    mimeType: string,
    originalName: string
  ): Promise<string> {
    const privateObjectDir = this.getPrivateObjectDir();
    const fileExtension = originalName.split('.').pop() || 'file';
    const objectId = `${randomUUID()}.${fileExtension}`;
    const fullPath = `${privateObjectDir}/uploads/${objectId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);
    const bucket = objectStorageClient.bucket(bucketName);
    const file = bucket.file(objectName);

    // Upload the buffer
    await file.save(buffer, {
      contentType: mimeType,
      metadata: {
        contentType: mimeType,
      },
    });

    // Set public ACL so the file can be accessed
    await setObjectAclPolicy(file, { 
      visibility: "public",
      owner: "system"
    });

    // Return normalized path
    return `/objects/uploads/${objectId}`;
  }
}

function parseObjectPath(path: string): {
  bucketName: string;
  objectName: string;
} {
  if (!path.startsWith("/")) {
    path = `/${path}`;
  }
  const pathParts = path.split("/");
  if (pathParts.length < 3) {
    throw new Error("Invalid path: must contain at least a bucket name");
  }

  const bucketName = pathParts[1];
  const objectName = pathParts.slice(2).join("/");

  return {
    bucketName,
    objectName,
  };
}

async function signObjectURL({
  bucketName,
  objectName,
  method,
  ttlSec,
}: {
  bucketName: string;
  objectName: string;
  method: "GET" | "PUT" | "DELETE" | "HEAD";
  ttlSec: number;
}): Promise<string> {
  const request = {
    bucket_name: bucketName,
    object_name: objectName,
    method,
    expires_at: new Date(Date.now() + ttlSec * 1000).toISOString(),
  };
  const response = await fetch(
    `${REPLIT_SIDECAR_ENDPOINT}/object-storage/signed-object-url`,
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(request),
    }
  );
  if (!response.ok) {
    throw new Error(
      `Failed to sign object URL, errorcode: ${response.status}, ` +
        `make sure you're running on Replit`
    );
  }

  const { signed_url: signedURL } = await response.json();
  return signedURL;
}

server/setlistFmService.ts.content:
/**
 * Setlist.fm API service for fetching historical concert data
 */

interface SetlistFmArtist {
  mbid: string;
  name: string;
  sortName: string;
  disambiguation?: string;
  url: string;
}

interface SetlistFmCity {
  id: string;
  name: string;
  state?: string;
  stateCode?: string;
  coords?: {
    lat: number;
    long: number;
  };
  country: {
    code: string;
    name: string;
  };
}

interface SetlistFmVenue {
  id: string;
  name: string;
  city: SetlistFmCity;
  url: string;
}

interface SetlistFmSong {
  name: string;
  cover?: {
    mbid: string;
    name: string;
    sortName: string;
    url: string;
  };
  info?: string;
  tape?: boolean;
}

interface SetlistFmSet {
  song: SetlistFmSong[];
  encore?: number;
}

interface SetlistFmSetlist {
  id: string;
  versionId: string;
  eventDate: string; // DD-MM-YYYY format
  lastUpdated: string;
  artist: SetlistFmArtist;
  venue: SetlistFmVenue;
  sets?: {
    set: SetlistFmSet[];
  };
  tour?: {
    name: string;
  };
  info?: string;
  url: string;
}

interface SetlistFmSearchResponse {
  type: string;
  itemsPerPage: number;
  page: number;
  total: number;
  setlist: SetlistFmSetlist[];
}

interface SetlistFmArtistSearchResponse {
  type: string;
  itemsPerPage: number;
  page: number;
  total: number;
  artist: SetlistFmArtist[];
}

interface SetlistFmVenueSearchResponse {
  type: string;
  itemsPerPage: number;
  page: number;
  total: number;
  venue: SetlistFmVenue[];
}

export interface SetlistFmSearchOptions {
  artistName?: string;
  artistMbid?: string;
  cityName?: string;
  countryCode?: string;
  date?: string; // DD-MM-YYYY format
  venueName?: string;
  venueId?: string;
  year?: string;
  state?: string;
  tourName?: string;
  p?: number; // page number, starts at 1
}

export class SetlistFmService {
  private readonly baseUrl = 'https://api.setlist.fm/rest/1.0';
  private readonly apiKey: string;

  constructor() {
    this.apiKey = process.env.SETLIST_FM_API_KEY || '';
  }

  /**
   * Search for setlists using Setlist.fm API
   */
  async searchSetlists(options: SetlistFmSearchOptions = {}): Promise<SetlistFmSetlist[]> {
    try {
      // Check if API key is available
      if (!this.apiKey) {
        console.warn('Setlist.fm API key not configured - returning empty results');
        return [];
      }

      const params = new URLSearchParams();

      // Add search parameters
      if (options.artistName) params.append('artistName', options.artistName);
      if (options.artistMbid) params.append('artistMbid', options.artistMbid);
      if (options.cityName) params.append('cityName', options.cityName);
      if (options.countryCode) params.append('countryCode', options.countryCode);
      if (options.date) params.append('date', options.date);
      if (options.venueName) params.append('venueName', options.venueName);
      if (options.venueId) params.append('venueId', options.venueId);
      if (options.year) params.append('year', options.year);
      if (options.state) params.append('state', options.state);
      if (options.tourName) params.append('tourName', options.tourName);
      if (options.p) params.append('p', String(options.p));

      const url = `${this.baseUrl}/search/setlists?${params.toString()}`;
      console.log('Fetching from Setlist.fm:', url.replace(this.apiKey, '[API_KEY]'));

      const response = await fetch(url, {
        headers: {
          'Accept': 'application/json',
          'x-api-key': this.apiKey,
          'User-Agent': 'ConcertCritic/1.0'
        },
      });

      if (!response.ok) {
        if (response.status === 404) {
          return []; // No results found
        }
        throw new Error(`Setlist.fm API error: ${response.status} ${response.statusText}`);
      }

      const data: SetlistFmSearchResponse = await response.json();
      return data.setlist || [];
    } catch (error) {
      console.error('Error fetching from Setlist.fm API:', error);
      // Return empty array instead of throwing to avoid breaking the combined search
      return [];
    }
  }

  /**
   * Search for artists using Setlist.fm API
   */
  async searchArtists(artistName: string, page = 1): Promise<SetlistFmArtist[]> {
    try {
      if (!this.apiKey) {
        console.warn('Setlist.fm API key not configured - returning empty results');
        return [];
      }

      const params = new URLSearchParams({
        artistName,
        p: String(page),
        sort: 'relevance'
      });

      const url = `${this.baseUrl}/search/artists?${params.toString()}`;

      const response = await fetch(url, {
        headers: {
          'Accept': 'application/json',
          'x-api-key': this.apiKey,
          'User-Agent': 'ConcertCritic/1.0'
        },
      });

      if (!response.ok) {
        if (response.status === 404) {
          return [];
        }
        throw new Error(`Setlist.fm API error: ${response.status} ${response.statusText}`);
      }

      const data: SetlistFmArtistSearchResponse = await response.json();
      return data.artist || [];
    } catch (error) {
      console.error('Error searching artists on Setlist.fm:', error);
      return [];
    }
  }

  /**
   * Search for venues using Setlist.fm API
   */
  async searchVenues(venueName: string, page = 1): Promise<SetlistFmVenue[]> {
    try {
      if (!this.apiKey) {
        console.warn('Setlist.fm API key not configured - returning empty results');
        return [];
      }

      const params = new URLSearchParams({
        name: venueName,
        p: String(page)
      });

      const url = `${this.baseUrl}/search/venues?${params.toString()}`;

      const response = await fetch(url, {
        headers: {
          'Accept': 'application/json',
          'x-api-key': this.apiKey,
          'User-Agent': 'ConcertCritic/1.0'
        },
      });

      if (!response.ok) {
        if (response.status === 404) {
          return [];
        }
        throw new Error(`Setlist.fm API error: ${response.status} ${response.statusText}`);
      }

      const data: SetlistFmVenueSearchResponse = await response.json();
      return data.venue || [];
    } catch (error) {
      console.error('Error searching venues on Setlist.fm:', error);
      return [];
    }
  }

  /**
   * Get setlists for a specific artist by MBID
   */
  async getArtistSetlists(artistMbid: string, page = 1): Promise<SetlistFmSetlist[]> {
    try {
      if (!this.apiKey) {
        console.warn('Setlist.fm API key not configured - returning empty results');
        return [];
      }

      const params = new URLSearchParams({
        p: String(page)
      });

      const url = `${this.baseUrl}/artist/${artistMbid}/setlists?${params.toString()}`;

      const response = await fetch(url, {
        headers: {
          'Accept': 'application/json',
          'x-api-key': this.apiKey,
          'User-Agent': 'ConcertCritic/1.0'
        },
      });

      if (!response.ok) {
        if (response.status === 404) {
          return [];
        }
        throw new Error(`Setlist.fm API error: ${response.status} ${response.statusText}`);
      }

      const data: SetlistFmSearchResponse = await response.json();
      return data.setlist || [];
    } catch (error) {
      console.error('Error fetching artist setlists from Setlist.fm:', error);
      return [];
    }
  }

  /**
   * Convert Setlist.fm date format (DD-MM-YYYY) to ISO format (YYYY-MM-DD)
   */
  private convertDateFormat(setlistDate: string): string {
    try {
      const [day, month, year] = setlistDate.split('-');
      return `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
    } catch (error) {
      console.warn('Invalid date format from Setlist.fm:', setlistDate);
      return setlistDate; // Return as-is if parsing fails
    }
  }

  /**
   * Extract setlist songs as a simple array of song names
   */
  private extractSetlist(setlist: SetlistFmSetlist): string[] {
    if (!setlist.sets?.set) return [];

    const songs: string[] = [];
    setlist.sets.set.forEach(set => {
      if (set.song) {
        set.song.forEach(song => {
          if (song.name) {
            songs.push(song.name);
          }
        });
      }
    });

    return songs;
  }

  /**
   * Transform Setlist.fm setlist to our Concert format
   */
  transformSetlist(setlist: SetlistFmSetlist): any {
    const venue = setlist.venue;
    const city = venue.city;
    const setlistSongs = this.extractSetlist(setlist);

    // Convert date from DD-MM-YYYY to YYYY-MM-DD
    const isoDate = this.convertDateFormat(setlist.eventDate);

    // Build city string with state/country info
    let cityString = city.name;
    if (city.stateCode) {
      cityString += `, ${city.stateCode}`;
    } else if (city.state) {
      cityString += `, ${city.state}`;
    }
    if (city.country?.code && city.country.code !== 'US') {
      cityString += `, ${city.country.code}`;
    }

    // Create description with setlist info
    let description = `${setlist.artist.name} performed at ${venue.name} in ${cityString}`;
    if (setlist.tour?.name) {
      description += ` (${setlist.tour.name})`;
    }
    if (setlistSongs.length > 0) {
      description += `. Setlist included ${setlistSongs.length} songs.`;
    }

    return {
      // Use Setlist.fm ID with a prefix to avoid conflicts
      id: `setlistfm_${setlist.id}`,
      artist: setlist.artist.name,
      venue: venue.name,
      city: cityString,
      date: isoDate,
      time: 'Historical', // Setlist.fm doesn't provide specific times
      price: 'Historical', // Historical concerts don't have current pricing
      genre: 'Music', // Setlist.fm doesn't categorize by genre
      imageUrl: null, // Setlist.fm doesn't provide images
      ticketUrl: setlist.url, // Link to setlist.fm page
      description,
      // Additional metadata specific to Setlist.fm
      setlistFmId: setlist.id,
      setlistFmUrl: setlist.url,
      artistMbid: setlist.artist.mbid,
      venueId: venue.id,
      setlist: setlistSongs,
      isHistorical: true,
      lastUpdated: setlist.lastUpdated,
      ...(setlist.tour?.name && { tourName: setlist.tour.name }),
      ...(setlist.info && { concertInfo: setlist.info }),
    };
  }

  /**
   * Comprehensive search method that combines multiple search strategies
   */
  async searchHistoricalConcerts(options: {
    search?: string;
    artist?: string;
    venue?: string;
    city?: string;
    year?: string;
    limit?: number;
    page?: number;
  } = {}): Promise<any[]> {
    const { search, artist, venue, city, year, limit = 20, page = 1 } = options;

    try {
      let searchOptions: SetlistFmSearchOptions = { p: page };

      // If there's a general search term, try to parse it intelligently
      if (search) {
        // Simple heuristics to determine what the search term might be
        searchOptions.artistName = search;

        // Also try venue search if no specific artist is provided
        if (!artist) {
          // We'll run multiple searches and combine results
        }
      }

      // Add specific parameters
      if (artist) searchOptions.artistName = artist;
      if (venue) searchOptions.venueName = venue;
      if (city) searchOptions.cityName = city;
      if (year) searchOptions.year = year;

      // Search for setlists
      const setlists = await this.searchSetlists(searchOptions);

      // Transform to our format
      const concerts = setlists.map(setlist => this.transformSetlist(setlist));

      // Apply limit
      return concerts.slice(0, limit);
    } catch (error) {
      console.error('Error searching historical concerts:', error);
      return [];
    }
  }
}

export const setlistFmService = new SetlistFmService();

